mapper.select:
SELECT boardNo, nickName, userNo, title, boardContent, createAt, b_readhit, isPinned 
FROM Board 
WHERE boardNo = #{boardNo}

mapper.update:
UPDATE Board
SET title = #{title},
    boardContent = #{boardContent},
    updateAt = CURRENT_TIMESTAMP,
    isPinned = #{isPinned}
WHERE boardNo = #{boardNo}

mapper.select:
SELECT * FROM ( SELECT RANK() OVER (ORDER BY isPinned DESC, boardNo DESC) AS no, v.* FROM ( SELECT b.boardNo, b.nickName, b.userNo, b.title, b.boardContent, b.createAt, b.b_readhit, b.isPinned, (SELECT COUNT(*) FROM Comments c WHERE c.boardNo = b.boardNo AND c.is_deleted = 0) AS commentCount FROM Board b
nickName LIKE '%' || #{nickName} || '%'
OR boardContent LIKE '%' || #{boardContent} || '%'
) v ) WHERE no BETWEEN #{start} AND #{end}

mapper.update:
UPDATE Board
SET isPinned = #{isPinned}
WHERE boardNo = #{boardNo}
