mapper.select:
SELECT boardNo, nickName, userNo, title, boardContent, createAt, b_readhit, isPinned 
FROM Board 
WHERE boardNo = #{boardNo}

mapper.update:
UPDATE Board
SET title = #{title},
    boardContent = #{boardContent},
    updateAt = CURRENT_TIMESTAMP,
    isPinned = #{isPinned}
WHERE boardNo = #{boardNo}

mapper.select:
